    function Simpan_level_2_opex(id, type,cek_add) {
        if (type == 'hapus_list_anggaran') {
         }
        if (type == 'update_nilai_level_2_opex') {
            saveData = 'update_nilai_level_2_opex';
            var url = "<?= base_url('admin/data_kontrak_penyedia_jasa/update_nilai_level_2_opex/') ?>" + id;
        }

        if (type == 'tambah_level_2_opex') {
            saveData = 'tambah_level_2_opex';
            var url = "<?= base_url('admin/data_kontrak_penyedia_jasa/tambah_nama_uraian_level_2_opex/') ?>" + id;
        }

        // INI UTNUK YANG ADDENDUM 1
        $.ajax({
            method: "POST",
            url: url,
            data: form_simpan_level_2_opex.serialize(),
            dataType: "JSON",
            beforeSend: function() {
                $('.button_simpan').addClass('disabled');
            },
            success: function(response) {
                if (response == 'success') {
