    function Simpan_level_10_bua(type) {
        if (type == 'update_nilai_level_10_bua') {
            saveData = 'update_nilai_level_10_bua';
            var url = "<?= base_url('admin/data_kontrak_penyedia_jasa/update_nilai_level_10_bua') ?>";
        }
        if (type == 'tambah_level_10_bua') {
            saveData = 'tambah_level_10_bua';
            var url = "<?= base_url('admin/data_kontrak_penyedia_jasa/tambah_nama_uraian_level_10_bua') ?>";
        }
        if (type == 'edit_level_10_bua') {
            saveData = 'edit_level_10_bua';
            var url = "<?= base_url('admin/data_kontrak_penyedia_jasa/edit_nama_uraian_level_10_bua') ?>";
        }

        $.ajax({
            method: "POST",
            url: url,
            data: form_simpan_level_10_bua.serialize(),
            dataType: "JSON",
            beforeSend: function() {
                $('.button_simpan').addClass('disabled');
            },
            success: function(response) {
                if (response == 'success') {
                    if (type == 'update_nilai_level_10_bua') {
                        $('.button_simpan').removeClass('disabled');
                        form_modal_level_10_bua.modal('hide')
                        message('success', 'Data Berhasil Di Tambah!', 'Berhasil')
                        location.reload()
                    }
                    if (type == 'tambah_level_10_bua') {
                        $('.button_simpan').removeClass('disabled');
                        form_modal_level_10_bua.modal('hide')
                        message('success', 'Data Berhasil Di Edit!', 'Berhasil')
                        location.reload()
                    }
                    if (type == 'edit_level_10_bua') {
                        $('.button_simpan').removeClass('disabled');
                        form_modal_level_10_bua.modal('hide')
                        message('success', 'Data Berhasil Di Edit!', 'Berhasil')
                        location.reload()
                    }
                }
            }
        })
    }

    function Question_level_10(ket, id, id_detail_bua_6) {
        Swal.fire({
            title: "Apakah Anda Yakin!?",
            text: 'Ingin Menghapus Data ' + ket + "?",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        }).then((willDelete) => {
            if (willDelete) {
                $.ajax({
                    type: "POST",
                    url: "<?= base_url('admin/data_kontrak_penyedia_jasa/hapus_induk_level_10_bua/') ?>" + id,
                    dataType: "JSON",
                    data: {
                        id_detail_bua_6: id_detail_bua_6
                    },
                    success: function(response) {
                        if (response == 'success') {
                            message('success', 'Berhasil!', 'Data Berhasil Di Hapus')
                            location.reload()
                        }
                    }
                })
            } else {
                message('success', 'Berhasil!', 'Data Tidak Jadi Di Hapus')
            }
        });
    }


