    function Question_level_5(ket, id, id_detail_sdm_1) {
        Swal.fire({
            title: "Apakah Anda Yakin!?",
            text: 'Ingin Menghapus Data ' + ket + "?",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        }).then((willDelete) => {
            if (willDelete) {
                $.ajax({
                    type: "POST",
                    url: "<?= base_url('admin/data_kontrak_penyedia_jasa/hapus_induk_level_5_sdm/') ?>" + id,
                    dataType: "JSON",
